//componente definida con clases
//
class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: "\\frac{x^3-3x+1}{x^2-2}",
      config: {
        charsThatBreakOutOfSupSub: "+-()",
        autoSubscriptNumerals: true,
        autoCommands: "pi sqrt",
      },
    };
  }
  render() {
    return (
      <EditableMathField // este se importó de mathQuill
        latex={this.state.latex}
        onChange={(mathField) => {
          this.setState({ latex: mathField.latex() });
        }}
      />
    );
  }
}

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = { date: new Date() };
  }

  componentDidMount() {
    this.timerId = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timerId);
  }

  tick() {
    this.setState({ date: new Date() });
  }

  render() {
    return (
      <div>
        <h1>¡La hora del mundo!</h1>
        <h2>la hora exacta es: {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}

const elemento = (
  <div>
    <Clock />
    <CajaMath />
  </div>
);
ReactDOM.render(elemento, document.getElementById("root"));
