import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { addStyles, EditableMathField } from "react-mathquill";
import $ from "jquery";
import Alert from "react-bootstrap/Alert";

import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";

addStyles();

/*$.get(
  "http://127.0.0.1:5000/api/v1/polynomial/properties/x**3+3*x+1",
  {},
  function (data) {
    console.log(data);
  }
);
*/
function filtro(e) {
  const teclasEsp = [
    "ArrowLeft",
    "ArrowRight",
    "ArrowUp",
    "ArrowDown",
    "Delete",
    "Enter",
    "Insert",
    "Backspace",
    "Home",
    "End",
  ];
  const caracAcep = "0123456789x+-/*()=.";
  if (teclasEsp.indexOf(e.key) !== -1 || caracAcep.indexOf(e.key) !== -1) {
    return;
  }
  // impide que se realice la acci贸n por defecto del evento
  e.preventDefault();
}
class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: "\\frac{x^3-3x+1}{x^2-2}",
      config: {
        charsThatBreakOutOfSupSub: "+-()",
        autoCommands: "pi sqrt",
      },
      //handlers: {
      //  edit: filtro,
      //},
    };
  }
  render() {
    return (
      <EditableMathField // este se import贸 de mathQuill
        latex={this.state.latex}
        config={this.state.config}
        onChange={(mathField) => {
          this.setState({ latex: mathField.latex() });
        }}
        onKeyDown={(event) => filtro(event)}
      />
    );
  }
}
function AlertDismissible() {
  const [show, setShow] = useState(true);

  return (
    <div>
      <Alert show={show} variant="success">
        <Alert.Heading>How's it going?!</Alert.Heading>
        <p>
          Duis mollis, est non commodo luctus, nisi erat porttitor ligula, eget
          lacinia odio sem nec elit. Cras mattis consectetur purus sit amet
          fermentum.
        </p>
        <hr />
        <div className="d-flex justify-content-end">
          <Button onClick={() => setShow(false)} variant="outline-success">
            Close me y'all!
          </Button>
        </div>
      </Alert>

      {!show && <Button onClick={() => setShow(true)}>Show Alert</Button>}
    </div>
  );
}

ReactDOM.render(<AlertDismissible />, document.getElementById("root"));
