// componentes definidos por el usuario

function Bienvenida(props) {
  return <h1>Hola, {props.name}</h1>;
}

function CajaMath(props) {
  return (
    <div>
      <p id={props.nombre}></p>
    </div>
  );
}

function App() {
  return (
    <div>
      <Bienvenida name="Sara" />
      <Bienvenida name="Cahal" />
      <Bienvenida name="Iker" />
      <CajaMath nombre="expr" />
      <EditableMathField
        latex={"\\frac{1}{\\sqrt{2}}\\cdot 2"} // latex value for the input field
        onChange={(mathField) => {
          // called everytime the input changes
          setState({ latex: mathField.latex() });
        }}
      />
    </div>
  );
}

//const element = <Bienvenida name="Sara" />;
ReactDOM.render(<App />, document.getElementById("root"));
