function tick() {
  const elemento = (
    <div>
      <h1>Hola, {formatName(usuario)}</h1>
      <h2>la hora exacta es:{new Date().toLocaleTimeString()}.</h2>
    </div>
  );

  ReactDOM.render(elemento, document.getElementById("root"));
}

setInterval(tick, 1000);
