// componentes definidos por el usuario

function Bienvenida(props) {
  return <h1>Hola, {props.name}</h1>;
}

class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: "\\frac{x^3-3x+1}{x^2-2}",
    };
  }
  render() {
    return (
      <EditableMathField
        latex={this.state.latex}
        onChange={(mathField) => {
          this.setState({ latex: mathField.latex() });
        }}
      />
    );
  }
}

function App() {
  return (
    <div>
      <Bienvenida name="Sara" />
      <Bienvenida name="Cahal" />
      <Bienvenida name="Iker" />
      <CajaMath />
    </div>
  );
}

//const element = <Bienvenida name="Sara" />;
ReactDOM.render(<App />, document.getElementById("root"));
