import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { addStyles, EditableMathField } from "react-mathquill";
import $ from "jquery";
import JXGBoard from "jsxgraph-react-js";
import Alert from "react-bootstrap/Alert";

import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavDropdown from "react-bootstrap/Navdropdown";
import Image from "react-bootstrap/Image";
import InputGroup from "react-bootstrap/InputGroup";

import "bootstrap/dist/css/bootstrap.min.css";
import TeXToLinealPyt from "./TeXToLineal";

addStyles();

/*$.get(
  "http://127.0.0.1:5000/api/v1/polynomial/properties/x**3+3*x+1",
  {},
  function (data) {
    console.log(data);
  }
);
*/
function filtro(e) {
  const teclasEsp = [
    "ArrowLeft",
    "ArrowRight",
    "ArrowUp",
    "ArrowDown",
    "Delete",
    "Enter",
    "Insert",
    "Backspace",
    "Home",
    "End",
  ];
  const caracAcep = "0123456789x+-/*()=.sqrt";
  if (teclasEsp.indexOf(e.key) !== -1 || caracAcep.indexOf(e.key) !== -1) {
    return;
  }
  // impide que se realice la acci贸n por defecto del evento
  e.preventDefault();
}
class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: props.latex,
      config: {
        charsThatBreakOutOfSupSub: "+-()",
        autoCommands: "pi sqrt",
      },
      resultados: {},
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    let cad = this.state.latex;
    cad = TeXToLinealPyt.TexToPyt(cad);
    console.log(cad);
    $.ajax({
      type: "GET",
      url: "http://127.0.0.1:5000/api/v1/polynomial/properties/" + cad,
      success: function (data) {
        console.log(data);
        //this.setState({ resultados: data });
      },
    });
  }

  render() {
    return (
      <InputGroup size="lg" className="mb-3">
        <InputGroup.Prepend>
          <InputGroup.Text id="basic-addon1">P(x) =</InputGroup.Text>
        </InputGroup.Prepend>
        <EditableMathField // este se import贸 de mathQuill
          latex={this.state.latex}
          config={this.state.config}
          onChange={(mathField) => {
            this.setState({ latex: mathField.latex() });
          }}
          onKeyDown={(event) => filtro(event)}
        />
        <InputGroup.Append>
          <Button variant="success" size="lg" block onClick={this.handleClick}>
            Obtener Propiedades
          </Button>
        </InputGroup.Append>
      </InputGroup>
    );
  }
}

let Grafica = (brd) => {
  brd.suspendUpdate();
  let p1 = brd.create("point", [-1, 1], { name: "A", size: 4 });
  let p2 = brd.create("point", [2, -1], { name: "B", size: 4 });
  let li = brd.create("line", [p1, p2], {
    strokeColor: "#00ff00",
    strokeWidth: 2,
  });
};

//let CalculaConSympy= (cadLatex) => {

//}

class BarraNav extends React.Component {
  render() {
    return (
      <Navbar bg="dark" variant="dark" expand="lg">
        <Navbar.Brand href="#home">Funciones Algebraicas:</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto text-light">
            <Nav.Link href="#home">Inicio</Nav.Link>
            <NavDropdown title="Funciones" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">
                Polinomiales
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Racionales</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Radicales</NavDropdown.Item>
              <NavDropdown.Divider />
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

class Cabeza extends React.Component {
  render() {
    return (
      <Container fluid>
        <Row bg="light">
          <Col className="align-self-center cinves-color bg-light font-italic">
            <h2>CalcVisual Web</h2>
          </Col>
          <Col md={{ span: 4, offset: 4 }}>
            <Image src="./logo.jpg" />
          </Col>
        </Row>
        <Row>
          <Col>
            <BarraNav />
          </Col>
        </Row>
      </Container>
    );
  }
}

class Cuerpo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latexFun: "\\frac{x^3-3x+1}{x^2-4}",
      propsFun: {},
    };
  }

  render() {
    return (
      <Container fluid>
        <Row>
          <Col sm={4}>
            <CajaMath latex={this.state.latexFun} />
          </Col>
          <Col sm={{ order: "last" }}>
            <JXGBoard
              logic={Grafica}
              boardAttributes={{ axis: true, boundingbox: [-10, 10, 10, -10] }}
              style={{
                width: "maxContent",
                height: "45em",
                border: "3px solid green",
              }}
            />
          </Col>
        </Row>
      </Container>
    );
  }
}

class Pagina extends React.Component {
  render() {
    return (
      <div>
        <Cabeza />
        <Cuerpo />
      </div>
    );
  }
}

ReactDOM.render(<Pagina />, document.getElementById("root"));
