import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { addStyles, EditableMathField } from "react-mathquill";
import $ from "jquery";
import Alert from "react-bootstrap/Alert";

import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavDropdown from "react-bootstrap/Navdropdown";
import Image from "react-bootstrap/Image";

import "bootstrap/dist/css/bootstrap.min.css";

addStyles();

/*$.get(
  "http://127.0.0.1:5000/api/v1/polynomial/properties/x**3+3*x+1",
  {},
  function (data) {
    console.log(data);
  }
);
*/
function filtro(e) {
  const teclasEsp = [
    "ArrowLeft",
    "ArrowRight",
    "ArrowUp",
    "ArrowDown",
    "Delete",
    "Enter",
    "Insert",
    "Backspace",
    "Home",
    "End",
  ];
  const caracAcep = "0123456789x+-/*()=.";
  if (teclasEsp.indexOf(e.key) !== -1 || caracAcep.indexOf(e.key) !== -1) {
    return;
  }
  // impide que se realice la acción por defecto del evento
  e.preventDefault();
}
class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: "\\frac{x^3-3x+1}{x^2-2}",
      config: {
        charsThatBreakOutOfSupSub: "+-()",
        autoCommands: "pi sqrt",
      },
      //handlers: {
      //  edit: filtro,
      //},
    };
  }
  render() {
    return (
      <EditableMathField // este se importó de mathQuill
        latex={this.state.latex}
        config={this.state.config}
        onChange={(mathField) => {
          this.setState({ latex: mathField.latex() });
        }}
        onKeyDown={(event) => filtro(event)}
      />
    );
  }
}

class Cabeza extends React.Component {
  render() {
    return (
      <Container fluid>
        <Row className="d-flex justify-content-around border border_success bg-light">
          <Col className="align-self-center cinves-color bg-light font-italic">
            <h2>CalcVisual Web</h2>
          </Col>
          <Col>
            <Image src="./logo.jpg" />
          </Col>
        </Row>
      </Container>
    );
  }
}

class BarraNav extends React.Component {
  render() {
    return (
      <Navbar bg="secondary" expand="lg">
        <Navbar.Brand href="#home" className="text-light">
          Funciones Algebraicas:
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto text-light">
            <Nav.Link href="#home" className="text-light">
              Inicio
            </Nav.Link>
            <NavDropdown
              title="Funciones"
              id="basic-nav-dropdown"
              bg="secondary"
              className="text-light"
            >
              <NavDropdown.Item href="#action/3.1">
                Polinomiales
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Racionales</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Radicales</NavDropdown.Item>
              <NavDropdown.Divider />
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      /*
      <Nav
        activeKey="/inicio"
        onSelect={(selectedKey) => alert(`selected ${selectedKey}`)}
      >
        <Nav.Item>
          <Nav.Link href="/ho">Funciones Algebraicas</Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link eventKey="link-1">Link</Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link eventKey="link-2">Link</Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link eventKey="disabled" disabled>
            Disabled
          </Nav.Link>
        </Nav.Item>
      </Nav>
      */
    );
  }
}
class Pagina extends React.Component {
  render() {
    return (
      <div>
        <Cabeza />
        <BarraNav />
      </div>
    );
  }
}

ReactDOM.render(<Pagina />, document.getElementById("root"));
