import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { addStyles, EditableMathField } from "react-mathquill";
import $ from "jquery";
import Alert from "react-bootstrap/Alert";

//import Button from "react-bootstrap/Button";
//import 'bootstrap/dist/css/bootstrap.min.css';

addStyles();

/*$.get(
  "http://127.0.0.1:5000/api/v1/polynomial/properties/x**3+3*x+1",
  {},
  function (data) {
    console.log(data);
  }
);
*/
function filtro(e) {
  const teclasEsp = [
    "ArrowLeft",
    "ArrowRight",
    "ArrowUp",
    "ArrowDown",
    "Delete",
    "Enter",
    "Insert",
    "Backspace",
    "Home",
    "End",
  ];
  const caracAcep = "0123456789x+-/*()=.";
  if (teclasEsp.indexOf(e.key) !== -1 || caracAcep.indexOf(e.key) !== -1) {
    return;
  }
  // impide que se realice la acci贸n por defecto del evento
  e.preventDefault();
}
class CajaMath extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latex: "\\frac{x^3-3x+1}{x^2-2}",
      config: {
        charsThatBreakOutOfSupSub: "+-()",
        autoCommands: "pi sqrt",
      },
      //handlers: {
      //  edit: filtro,
      //},
    };
  }
  render() {
    return (
      <EditableMathField // este se import贸 de mathQuill
        latex={this.state.latex}
        config={this.state.config}
        onChange={(mathField) => {
          this.setState({ latex: mathField.latex() });
        }}
        onKeyDown={(event) => filtro(event)}
      />
    );
  }
}

function Example() {
  return (
    <Alert dismissible variant="danger">
      <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
      <p>Change this and that and try again.</p>
    </Alert>
  );
}

ReactDOM.render(Example, document.getElementById("root"));
